    private void btn_hapusdataActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_hapusdataActionPerformed
        // TODO add your handling code here:
        int i = tbsiswa.getSelectedRow();
        if(i== -1){
            return;
        }
        String kode = (String) tbsiswa.getValueAt(i, 0);
        String nis = (String) tbsiswa.getValueAt(i, 1);
        String nama = (String) tbsiswa.getValueAt(i, 2);

        ModelSiswa modg = new ModelSiswa();
        modg.setKode(kode);
        modg.setNis(nis);
        modg.setNama_siswa(nama);

        int konfirmasiHapus = JOptionPane.showConfirmDialog(null, "Apakah anda yakin akan menghapus data siswa "
            + "dengan \n Kode Absen "+kode+" Untuk NIP "+nis+" yang bernama "+nama+"? ", "Hapus Guru", JOptionPane.YES_NO_OPTION);
        if(konfirmasiHapus == JOptionPane.YES_OPTION){
            try{
                ManajemenSiswa mang = new ManajemenSiswa();
                mang.hapusSiswa(modg);
                JOptionPane.showMessageDialog(null, "Data berhasil dihapus", "Berhasil Menghapus Data",JOptionPane.INFORMATION_MESSAGE);
            }
            catch(SQLException ex){
                JOptionPane.showMessageDialog(null, "Terjadi kesalahan menghapus data","Kesalahan Menghapus Data",JOptionPane.ERROR_MESSAGE);
                Logger.getLogger(FormSiswa.class.getName()).log(Level.SEVERE, null, ex);
            }
            finally{
                try {
                    loadTblSiswa();
                    txtkode.setEnabled(false);
                    txtkode.setText("");

                    txtnis.setEnabled(false);
                    txtnis.setText("");

                    txtnama.setEnabled(false);
                    txtnama.setText("");

                    cmbkelas.setEnabled(false);
                    cmbkelas.setSelectedIndex(0);
                    cmbkelamin.setEnabled(false);
                    cmbkelamin.setSelectedIndex(0);
                    txttlp.setEnabled(false);
                    txttlp.setText("");

                   
                    txtfoto.setEnabled(false);
                    txtfoto.setText("");

                } catch (SQLException ex) {
                    Logger.getLogger(FormSiswa.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
        }
    }//GEN-LAST:event_btn_hapusdataActionPerformed

